mapper.select:
SELECT COUNT(acc_id) AS cnt
FROM account
WHERE acc_id=#{acc_id}

SELECT COUNT(acc_name) AS cnt
FROM account
WHERE acc_name=#{acc_name}

SELECT COUNT(acc_no) as cnt
FROM account
WHERE acc_id=#{acc_id} AND acc_pw=#{acc_pw}

mapper.update:
UPDATE account
SET acc_name=#{acc_name}, acc_tel=#{acc_tel}, acc_age=#{acc_age}
WHERE acc_no=#{acc_no}

UPDATE account
SET acc_img=#{acc_img}, acc_saved_img=#{acc_saved_img},
acc_thumb_img=#{acc_thumb_img}, acc_img_size=#{acc_img_size}
WHERE acc_no=#{acc_no}

mapper.select:
SELECT COUNT(acc_no) as cnt
FROM account
WHERE acc_no=#{acc_no} AND acc_pw=#{acc_pw}

mapper.update:
UPDATE account
SET acc_pw=#{acc_pw}
WHERE acc_no=#{acc_no}

UPDATE account
SET acc_pw=#{acc_pw}
WHERE acc_id=#{acc_id}

mapper.delete:
DELETE FROM share_mark
WHERE acc_no = #{acc_no}
AND scon_no = #{scon_no}

DELETE FROM qna_mark
WHERE acc_no = #{acc_no}
AND qcon_no = #{qcon_no}

mapper.select:
SELECT count(*)
FROM share_comment
WHERE scon_no = #{scon_no}

SELECT count(*)
FROM qna_comment
WHERE qcon_no = #{qcon_no}
