mapper.select:
SELECT COUNT(adm_id) AS cnt
FROM admin
WHERE adm_id=#{adm_id}

SELECT COUNT(adm_name) AS cnt
FROM admin
WHERE adm_name=#{adm_name}

SELECT COUNT(adm_email) AS cnt
FROM admin
WHERE adm_email=#{adm_email}

SELECT COUNT(adm_no) as cnt
FROM admin
WHERE adm_id=#{adm_id} AND adm_pw=#{adm_pw}

SELECT * FROM ( SELECT l.adm_log_no, a.adm_no, a.adm_id, l.adm_log_ip, l.adm_log_time, ROW_NUMBER() OVER (ORDER BY l.adm_log_time) AS r FROM admin_log l INNER JOIN admin a ON a.adm_no = l.adm_no
AND LOWER(a.adm_id) LIKE '%' || LOWER(#{word_id}) || '%'
AND l.adm_log_ip LIKE '%' || #{word_ip} || '%'
AND l.adm_log_time BETWEEN TO_TIMESTAMP(#{start_date} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND TO_TIMESTAMP(#{end_date} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT * FROM ( SELECT l.acc_log_no, a.acc_no, a.acc_id, l.acc_log_ip, l.acc_log_time, ROW_NUMBER() OVER (ORDER BY l.acc_log_time) AS r FROM acc_log l INNER JOIN account a ON a.acc_no = l.acc_no
AND LOWER(a.acc_id) LIKE '%' || LOWER(#{word_id}) || '%'
AND l.acc_log_ip LIKE '%' || #{word_ip} || '%'
AND l.acc_log_time BETWEEN TO_TIMESTAMP(#{start_date} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND TO_TIMESTAMP(#{end_date} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
) WHERE r >= #{start_num} AND r <= #{end_num}
