mapper.select:
SELECT cate_no, cate_name, cate_cnt, cate_seqno, cate_visible
FROM category
WHERE cate_no = #{cate_no}

mapper.update:
UPDATE category
SET cate_name=#{cate_name}, cate_seqno = #{cate_seqno}, cate_visible=#{cate_visible}
WHERE cate_no = #{cate_no}

UPDATE category
SET cate_seqno = cate_seqno - 1
WHERE cate_no = #{cate_no}

UPDATE category
SET cate_seqno = cate_seqno + 1
WHERE cate_no = #{cate_no}

UPDATE category
SET cate_visible='Y'
WHERE cate_no=#{cate_no}

UPDATE category
SET cate_visible='N'
WHERE cate_no=#{cate_no}

mapper.select:
SELECT cate_no, cate_name, cate_cnt, cate_seqno, cate_visible, r FROM ( SELECT cate_no, cate_name, cate_cnt, cate_seqno, cate_visible, rownum as r FROM ( SELECT cate_no, cate_name, cate_cnt, cate_seqno, cate_visible FROM category
WHERE UPPER(cate_name) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY cate_seqno ASC ) ) WHERE r > = #{start_num} AND r < = #{end_num}
