mapper.update:
UPDATE comment_like
SET mark='Y'
where scmt_no=#{scmt_no} and acc_no=#{acc_no}

UPDATE comment_like
set mark='N'
where scmt_no=#{scmt_no} and acc_no=#{acc_no}

mapper.select:
SELECT like_no, scon_no, acc_no, scmt_no ,mark
FROM comment_like
WHERE scon_no=#{scon_no} and acc_no=#{acc_no}
