mapper.select:
SELECT qcon_no, cate_no, acc_no, qcon_name, qcon_contents, qcon_views, qcon_bookmark, qcon_bookcnt, qcon_comment, qcon_date, qcon_passwd
FROM qna_contents
WHERE qcon_no=#{qcon_no}

SELECT qcon_no, cate_no, acc_no, qcon_name, qcon_contents, qcon_views, qcon_bookmark, qcon_bookcnt, qcon_comment, qcon_date, qcon_passwd
FROM qna_contents
WHERE qcon_no= #{qcon_no}

SELECT file_no, qcon_no, file_origin_name, file_upload_name, file_thumb_name, file_size, file_date
FROM qna_image
WHERE qcon_no=#{qcon_no}

SELECT COUNT(*) as cnt FROM qna_contents
WHERE cate_no=#{cate_no}
WHERE cate_no=#{cate_no} AND (UPPER(qcon_name) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(qcon_contents) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT qcon_no, cate_no, acc_no, qcon_name, qcon_contents, qcon_views, qcon_bookmark, qcon_bookcnt, qcon_comment, qcon_date, qcon_passwd, r FROM ( SELECT qcon_no, cate_no, acc_no, qcon_name, qcon_contents, qcon_views, qcon_bookmark, qcon_bookcnt, qcon_comment, qcon_date, qcon_passwd, rownum as r FROM ( SELECT qcon_no, cate_no, acc_no, qcon_name, qcon_contents, qcon_views, qcon_bookmark, qcon_bookcnt, qcon_comment, qcon_date, qcon_passwd FROM qna_contents
WHERE cate_no=#{cate_no}
WHERE cate_no=#{cate_no} AND (UPPER(qcon_name) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(qcon_contents) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY qcon_no DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM qna_contents
WHERE qcon_no = #{qcon_no} AND qcon_passwd=#{qcon_passwd}

mapper.update:
UPDATE qna_contents
SET qcon_views = qcon_views+1
WHERE qcon_no=#{qcon_no}

UPDATE qna_contents
SET qcon_name=#{qcon_name}, qcon_contents=#{qcon_contents}
WHERE qcon_no = #{qcon_no}

UPDATE qna_image
SET file_origin_name=#{file_origin_name}, file_upload_name=#{file_upload_name}, file_thumb_name=#{file_thumb_name},file_size=#{file_size}
WHERE file_no=#{file_no}

mapper.select:
SELECT file_no, qcon_no, file_origin_name, file_upload_name, file_thumb_name, file_size, file_date
FROM qna_image
WHERE qcon_no=#{qcon_no}

SELECT COUNT(*) as cnt 
FROM qcon_no 
WHERE cate_no=#{cate_no}

SELECT COUNT(*) as cnt 
FROM qcon_no 
WHERE acc_no=#{acc_no}

SELECT qcmt_no, qcon_no, acc_no, qcmt_contents, qcmt_date
FROM qna_comment
WHERE qcmt_no=#{qcmt_no}

SELECT count(*)
FROM qna_comment
WHERE qcon_no=#{qcon_no}

mapper.update:
UPDATE qna_contents
SET qcon_comment = qcon_comment + 1
WHERE qcon_no = #{qcon_no}

UPDATE qna_contents
SET qcon_comment = qcon_comment - 1
WHERE qcon_no = #{qcon_no}

mapper.select:
SELECT count(*)
FROM qna_mark
WHERE qcon_no=#{qcon_no}

SELECT acc_no, qcon_no
FROM qna_mark
WHERE acc_no=#{acc_no} AND qcon_no=#{qcon_no}

mapper.update:
