WHERE acc_no=#{acc_no} AND qcon_no=#{qcon_no}

mapper.select:
SELECT a.acc_id
FROM account a, qna_contents q 
WHERE a.acc_no=q.acc_no AND q.qcon_no=#{qcon_no}

